settings.custom_css:
.newsletter__wrapper {
    padding: 120px 0;
    max-width: 530px;
}
.newsletter__heading {
margin-bottom: 16px;
font-size: 28px;
}
.newsletter__subheading {
margin-bottom: 40px;
}
.newsletter-form {
    border: 2px solid rgba(0, 0, 0, 0.18);
    align-items: center;
    height: 60px;
}
.newsletter-form .field .control {
width: auto;
}
.field {
margin-bottom: 0;
height: 100%;
}
.control {
height: 100%;
}
.field:nth-child(2) .control {
border-left: 2px solid rgba(0, 0, 0, 0.18);
}
button.button {
border: none;
height: 100%;
text-decoration: underline;
}
input.input {
border: none;
height: 100%;
background: transparent;
}
