settings.custom_css:
.flickity-button {
  border: 0px !important;
#background: transparent;
}

.flickity-prev-next-button .flickity-button-icon {
	position: absolute;
	left: 25%;
	top: 25%;
	width: 50%;
	height: 50%;
}

.newsletter__wrapper {
    padding: 120px 0;
    max-width: 530px;
}
.newsletter__heading {
margin-bottom: 16px;
font-size: 28px;
}
.newsletter__subheading {
margin-bottom: 40px;
}
.newsletter-form {
    border: 2px solid rgba(0, 0, 0, 0.18);
    align-items: center;
    height: 60px;
}
.newsletter-form .field .control {
width: auto;
}
.field {
margin-bottom: 0;
height: 100%;
}
.control {
height: 100%;
}
.field:nth-child(2) .control {
border-left: 2px solid rgba(0, 0, 0, 0.18);
}
button.button {
border: none;
height: 100%;
text-decoration: underline;
}
input.input {
border: none;
height: 100%;
background: transparent;
}

settings.css_desktop:
.subtitle strong {
	color: inherit;
	font-weight: 600;
	font-size: 30px;
}

#shopify-section-template--15804869935270__main .banner__heading {
  font-size: 60px;
  font-family: 'Didot';
}


#shopify-section-template--15804869935270__16576815583eb08556 .image-element__wrap img {
  max-height: 1320px;
  object-fit: cover;
  object-position: center;
}
#shopify-section-template--15804869935270__main .image-element__wrap img {
  max-height: 850px;
    object-fit: cover;
    object-position: center;
}

settings.css_mobile:
.subtitle strong {
	color: inherit;
	font-weight: 600;
	font-size: 25px;
}


#shopify-section-template--15804869935270__main .banner__heading {
  font-size: 40px;
  font-family: 'Didot';
}

#shopify-section-template--15804869935270__16576815583eb08556 .caption-content {
	background-color: rgba(255, 255, 255, 0.0);
	border: 0px solid #ffffff;
	padding: 100px 32px 0;
}
#shopify-section-template--15804869935270__16576815583eb08556.image-with-text-overlay .mobile-text--below-media>.container {
  display:block;
}
#shopify-section-template--15804869935270__main .image-element__wrap img {
  max-height: 865px;
    object-fit: cover;
    object-position: top;
}

#shopify-section-template--15804869935270__16576815583eb08556 .image-element__wrap img {
  max-height: unset;
}



#shopify-section-template--15804869935270__16576815583eb08556 .is-hidden-desktop-only .image-element__wrap {
  max-height: 1325px;
min-height: 1320px;
background-color: #f0d3b5 !important;
}
#shopify-section-template--15804869935270__1657682484d4e42fd5 {
    padding-top: 20px;
    padding-bottom: 0px;
}
